         oPayment.Send.Items.Add(new Item() { Name = "<<Product Name>>", Price = Decimal.Parse("<<Unit Price>>"), Currency = "<<Currency>>", Quantity = Int32.Parse("<<Quantity>>"), URL = "<<Product Detail URL>>" });

